INSERT INTO `ideological_case` (`id`, `created_at`, `updated_at`, `title`, `content`, `software_engineering_chapter`, `ideological_theme`, `case_type`, `tags`, `key_points`, `discussion_questions`, `teaching_suggestions`, `difficulty_level`, `usage_count`, `rating`, `rating_count`, `is_public`, `status`, `reviewer_comment`, `author_id`, `reviewer_id`) VALUES (20, '2025-12-04 18:13:56.868418', '2025-12-04 18:13:56.868418', '需求分析中的用户思维：12306购票系统的演进', '12306铁路购票系统的发展历程体现了以用户为中心的需求分析理念。从最初的系统崩溃、验证码过于复杂，到后来不断优化用户体验，体现了软件工程师对用户需求的深入理解和人文关怀。\n\n系统初期，开发团队更多关注技术实现，忽视了用户体验。大量用户投诉后，团队开始重视用户反馈，简化购票流程，优化验证码设计，增加候补购票功能。这个过程展现了从技术导向到用户导向的转变。\n\n这个案例告诉我们：优秀的软件工程师要有同理心，要站在用户角度思考问题，将技术服务于人的需求。', '需求分析', '人文素养', 'case_study', '[\"用户体验\", \"需求分析\", \"12306\", \"以人为本\"]', '[\"用户需求获取方法\", \"用户体验设计\", \"需求优先级\"]', '[\"如何在需求分析中体现以用户为中心的理念？\", \"技术实现和用户体验如何平衡？\", \"如何有效收集和分析用户反馈？\"]', '通过12306系统的演进历程，引导学生理解用户思维的重要性。可以组织用户访谈和需求分析实践。', 2, 0, 0, 0, 1, 'published', NULL, 1, NULL);
INSERT INTO `ideological_case` (`id`, `created_at`, `updated_at`, `title`, `content`, `software_engineering_chapter`, `ideological_theme`, `case_type`, `tags`, `key_points`, `discussion_questions`, `teaching_suggestions`, `difficulty_level`, `usage_count`, `rating`, `rating_count`, `is_public`, `status`, `reviewer_comment`, `author_id`, `reviewer_id`) VALUES (21, '2025-12-04 18:13:56.868418', '2025-12-04 18:13:56.868418', '软件测试中的责任意识：波音737 MAX的教训', '波音737 MAX飞机的两次坠机事故，暴露了软件测试中的严重问题。MCAS系统的设计缺陷和测试不充分，导致了悲剧的发生。这个案例深刻警示我们：软件测试不仅是技术问题，更是责任问题。\n\n调查显示，波音在软件开发过程中存在多个问题：测试覆盖不足、风险评估不充分、对飞行员培训重视不够。这些问题的根源在于缺乏责任意识，过度追求商业利益而忽视安全。\n\n作为软件工程师，我们要认识到：我们编写的代码可能关系到用户的生命安全，必须以高度的责任心对待每一行代码、每一个测试用例。', '软件测试', '责任担当', 'case_study', '[\"软件安全\", \"测试\", \"波音\", \"责任\"]', '[\"测试的重要性\", \"安全关键系统\", \"风险评估\"]', '[\"如何在软件测试中体现责任意识？\", \"商业压力和质量保证如何平衡？\", \"如何建立有效的测试文化？\"]', '通过波音737 MAX案例，引导学生认识软件测试的重要性和工程师的社会责任。可以组织测试计划设计实践。', 4, 0, 0, 0, 1, 'published', NULL, 1, NULL);
