-- ----------------------------
-- Table structure for prompt_template
-- ----------------------------
DROP TABLE IF EXISTS `prompt_template`;
CREATE TABLE `prompt_template` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `created_at` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6),
  `updated_at` datetime(6) NOT NULL DEFAULT CURRENT_TIMESTAMP(6) ON UPDATE CURRENT_TIMESTAMP(6),
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '模板名称',
  `description` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '模板描述',
  `template_type` varchar(21) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '模板类型',
  `template_content` longtext CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '模板内容',
  `variables` json NOT NULL COMMENT '模板变量',
  `category` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '分类',
  `software_engineering_chapter` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '适用章节',
  `ideological_theme` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci DEFAULT NULL COMMENT '思政主题',
  `usage_count` int NOT NULL DEFAULT '0' COMMENT '使用次数',
  `is_system` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否系统模板',
  `is_active` tinyint(1) NOT NULL DEFAULT '1' COMMENT '是否启用',
  `rating` double NOT NULL DEFAULT '0' COMMENT '评分(0-5)',
  `rating_count` int NOT NULL DEFAULT '0' COMMENT '评分人数',
  `creator_id` bigint DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `fk_prompt_t_user_f74eb6b9` (`creator_id`) USING BTREE,
  KEY `idx_prompt_temp_created_bf7a5c` (`created_at`) USING BTREE,
  KEY `idx_prompt_temp_updated_c0e4a3` (`updated_at`) USING BTREE,
  KEY `idx_prompt_temp_name_941a49` (`name`) USING BTREE,
  KEY `idx_prompt_temp_templat_8bae9a` (`template_type`) USING BTREE,
  KEY `idx_prompt_temp_categor_14337a` (`category`) USING BTREE,
  KEY `idx_prompt_temp_softwar_112694` (`software_engineering_chapter`) USING BTREE,
  KEY `idx_prompt_temp_ideolog_e36424` (`ideological_theme`) USING BTREE,
  KEY `idx_prompt_temp_usage_c_fbd1b0` (`usage_count`) USING BTREE,
  KEY `idx_prompt_temp_is_syst_d91c18` (`is_system`) USING BTREE,
  KEY `idx_prompt_temp_is_acti_8b5a28` (`is_active`) USING BTREE,
  KEY `idx_prompt_temp_rating_0729e6` (`rating`) USING BTREE,
  KEY `idx_prompt_temp_rating__5eed95` (`rating_count`) USING BTREE,
  CONSTRAINT `fk_prompt_t_user_f74eb6b9` FOREIGN KEY (`creator_id`) REFERENCES `user` (`id`) ON DELETE SET NULL ON UPDATE RESTRICT
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci ROW_FORMAT=DYNAMIC COMMENT='提示词模板库';

